INSERT INTO `` (`id`,`name`,`sqlText`,`columnMap`,`metadata`,`uploadType`,`filters`) VALUES (591,'ccfap_filtered:chart','\nSELECT\n concat(\n   case when @county_filter=\'-- All --\' then \'Vermont\' else `County` end\n ) as label,\n YEAR(`VISION Payment Date`) as cat,\n count(*) as value\n FROM data_ccfap_stars\n where YEAR(`VISION Payment Date`) > 0\n and `County`=(case when @county_filter=\'-- All --\' then `County` else @county_filter end)\n and `Provider Program Type`=(case when @program_filter=\'-- All --\' then `Provider Program Type` else @program_filter end)\n and (\n   @stars_filter=\'-- All --\' or\n   @stars_filter=\'4 or 5 Star\' and `Current Tier Level` in (\'4 Star\', \'5 Star\') or\n   `Current Tier Level` = @stars_filter\n )\n group by label, cat',NULL,'{\"yAxis\": {\"type\": \"number\"}, \"filters\": [{\"key\": \"county_filter\", \"title\": \"County\"}, {\"key\": \"program_filter\", \"title\": \"Program\"}, {\"key\": \"stars_filter\", \"title\": \"Tier Level\"}]}','general:ccfap_stars','{\"table\": \"data_ccfap_stars\", \"extra_filter_values\": {\"stars_filter\": [\"4 or 5 Star\"]}, \"filters\": {\"county_filter\": {\"column\": \"County\"}, \"program_filter\": {\"column\": \"Provider Program Type\"}, \"stars_filter\": {\"column\": \"Current Tier Level\"}}}');
INSERT INTO `` (`id`,`name`,`sqlText`,`columnMap`,`metadata`,`uploadType`,`filters`) VALUES (592,'ccfap_filtered_byprog:chart','\nWITH data_ccfap_stars_month_year as (\n  SELECT t.*, month(`VISION Payment Date`) as `calc_month`, year(`VISION Payment Date`) as `calc_year` from data_ccfap_stars t\n)\nSELECT\n concat(\n   case when @county_filter=\'-- All --\' then \'Vermont\' else `County` end\n ) as label,\n `Provider Program Type` as cat\n , count(*) as value\n FROM data_ccfap_stars_month_year\n where YEAR(`VISION Payment Date`) > 0\n and `County`=(case when @county_filter=\'-- All --\' then `County` else @county_filter end)\n and `calc_month`=(case when @month_filter=\'-- All --\' then `calc_month` else @month_filter end)\n and `calc_year`=(case when @year_filter=\'-- All --\' then `calc_year` else @year_filter end)\n and (\n   @stars_filter=\'-- All --\' or\n   @stars_filter=\'4 or 5 Star\' and `Current Tier Level` in (\'4 Star\', \'5 Star\') or\n   `Current Tier Level` = @stars_filter\n )\n group by label, cat\n',NULL,'{\"yAxis\": {\"type\": \"number\"}, \"filters\": [{\"key\": \"county_filter\", \"title\": \"County\"}, {\"key\": \"month_filter\", \"title\": \"Month\"}, {\"key\": \"year_filter\", \"title\": \"Year\"}, {\"key\": \"stars_filter\", \"title\": \"Tier Level\"}]}','general:ccfap_stars','{\"table\": \"data_ccfap_stars\", \"extra_filter_values\": {\"stars_filter\": [\"4 or 5 Star\"]}, \"filters\": {\"county_filter\": {\"column\": \"County\"}, \"month_filter\": {\"column\": \"month\", \"sort\": \"number\"}, \"year_filter\": {\"column\": \"year\", \"sort\": \"number\"},  \"stars_filter\": {\"column\": \"Current Tier Level\"}}}');
INSERT INTO `` (`id`,`name`,`sqlText`,`columnMap`,`metadata`,`uploadType`,`filters`) VALUES (593,'ccfap_filtered_byprog2:chart','\nWITH data_ccfap_stars_yyyymm as (\n  SELECT t.*, DATE_FORMAT(`VISION Payment Date`, \'%Y-%m\') AS `yyyymm` from data_ccfap_stars t\n)\nSELECT\n concat(\n   case when @county_filter=\'-- All --\' then \'Vermont\' else `County` end\n ) as label,\n `Provider Program Type` as cat\n , count(*) as value\n FROM data_ccfap_stars_yyyymm\n where YEAR(`VISION Payment Date`) > 0\n and `County`=(case when @county_filter=\'-- All --\' then `County` else @county_filter end)\n and `yyyymm` >= (case when @from_filter=\'undefined--- All --\' then `yyyymm` else @from_filter end)\n and `yyyymm` <= (case when @to_filter=\'undefined--- All --\' then `yyyymm` else @to_filter end)\n and (\n   @stars_filter=\'-- All --\' or\n   @stars_filter=\'4 or 5 Star\' and `Current Tier Level` in (\'4 Star\', \'5 Star\') or\n   `Current Tier Level` = @stars_filter\n )\n group by label, cat\n',NULL,'{\"yAxis\": {\"type\": \"number\"}, \"filters\": [{\"key\": \"county_filter\", \"title\": \"County\"}, {\"xf\": \"datetime-to-mmyyyy\", \"key\": \"from_filter\", \"title\": \"From\"}, {\"xf\": \"datetime-to-mmyyyy\", \"key\": \"to_filter\", \"title\": \"To\"}, {\"key\": \"stars_filter\", \"title\": \"Tier Level\"}]}','general:ccfap_stars','{\"table\": \"data_ccfap_stars\", \"extra_filter_values\": {\"stars_filter\": [\"4 or 5 Star\"]}, \"filters\": {\"county_filter\": {\"column\": \"County\"}, \"from_filter\": {\"column\": \"VISION Payment Date\", \"sort\": \"mmyyyy\", \"xf\": \"datetime-to-mmyyyy\"}, \"to_filter\": {\"column\": \"VISION Payment Date\", \"sort\": \"mmyyyy\", \"xf\": \"datetime-to-mmyyyy\"},  \"stars_filter\": {\"column\": \"Current Tier Level\"}}}');
